
document.addEventListener("DOMContentLoaded",function(){const hamburgerIcon=document.querySelector('.hamburger-icon');const navbar=document.getElementById('navbarMobile');if(window.innerWidth>1024){navbar.classList.add('active');}
hamburgerIcon.addEventListener('click',function(){navbar.classList.toggle('active');});window.addEventListener('resize',function(){if(window.innerWidth>1024){navbar.classList.add('active');}else{navbar.classList.remove('active');}});});document.addEventListener("DOMContentLoaded",function(){const dropdownButton=document.getElementById('dropdownButton');const dropdownContent=document.querySelector('.dropdown-content');let dropdownVisible=false;dropdownButton.addEventListener('click',function(){dropdownVisible=!dropdownVisible;if(dropdownVisible){dropdownContent.style.display='block';}else{dropdownContent.style.display='none';}});});document.addEventListener('DOMContentLoaded',function(){var agendaButton=document.querySelector('.agendaButton');var onsGeriefButton=document.querySelector('.onsGeriefButton');var paloebiesButton=document.querySelector('.paloebiesButton');var wolvenButton=document.querySelector('.wolvenButton');var poemaButton=document.querySelector('.poemaButton');var jvpvButton=document.querySelector('.jvpvButton');var kevinButton=document.querySelector('.kevinButton');agendaButton.addEventListener('click',function(){scrollToPage(3);});onsGeriefButton.addEventListener('click',function(){scrollToPage(4);});paloebiesButton.addEventListener('click',function(){scrollToPage(9);});wolvenButton.addEventListener('click',function(){scrollToPage(13);});poemaButton.addEventListener('click',function(){scrollToPage(15);});jvpvButton.addEventListener('click',function(){scrollToPage(20);});kevinButton.addEventListener('click',function(){scrollToPage(24);});function scrollToPage(page){document.querySelector('iframe').src=document.querySelector('iframe').src.replace(/page=.*&/,'page='+page+'&');console.log(document.querySelector('iframe').src);}});